
public class conditional {

	public static int getDays(String monthName) {
		// TODO Auto-generated method stub
		int days=0;
		switch(monthName){
		case "January": days = 31 ;break;	
		
		case "February": days  = 28 ;break;

		case "March": days = 31 ;break;
		}
		return days;}
	}

